export const LOGIN = 'LOGIN';

const LOGIN_ACTION = (payload) => ({
  type: LOGIN,
  payload,
});

const fetchToken = async () => {
  const firstFetch = await fetch('https://opentdb.com/api_token.php?command=request');
  const token = await firstFetch.json();
  return token.token;
};

const fetchGame = async (token) => {
  const secondFetch = await fetch(`https://opentdb.com/api.php?amount=5&token=${token}`);
  const response = await secondFetch.json();
  return response;
};

export const fetchTrivia = (localState) => async (dispatch) => {
  console.log(localState);
  const magicNumber = 3;
  const localStorageToken = localStorage.getItem('token');
  if (localStorageToken) {
    const response = fetchGame(localStorageToken);
    if (response.response_code === magicNumber) {
      const token = await fetchToken();
      localStorage.setItem('token', token);
      return fetchGame(token);
    }
  }
  const token = await fetchToken();
  localStorage.setItem('token', token);
  return fetchGame(token);
};
