import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { fetchTrivia, loginAction } from '../redux/actions';

class Login extends Component {
  state = {
    email: '',
    name: '',
    disabled: true,
  };

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value }, () => {
      if (this.validation()) {
        this.setState({ disabled: false });
      } else {
        this.setState({ disabled: true });
      }
    });
  };

  handleClick = () => {
    const { dispatch, history } = this.props;
    dispatch(loginAction(this.state));
    dispatch(fetchTrivia());
    history.push('./game');
  };

  validation = () => {
    const { email, name } = this.state;
    const magicNumber = 1;
    const regex = /^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/g;
    const returnRegex = regex.test(email);
    const returnPass = name.length >= magicNumber;
    return !!(returnPass && returnRegex);
  };

  render() {
    const { email, name, disabled } = this.state;
    const { history } = this.props;
    return (
      <div>
        <form>
          <input
            name="name"
            value={ name }
            type="text"
            data-testid="input-player-name"
            onChange={ this.handleChange }
          />
          <input
            name="email"
            value={ email }
            type="email"
            data-testid="input-gravatar-email"
            onChange={ this.handleChange }
          />
          <button
            disabled={ disabled }
            type="button"
            data-testid="btn-play"
            onClick={ this.handleClick }
          >
            Play

          </button>
          <button
            type="button"
            data-testid="btn-settings"
            onClick={ () => history.push('/setting') }
          >
            Settings

          </button>
        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  ...state.player,
});

Login.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func.isRequired,
  }).isRequired,
  dispatch: PropTypes.func.isRequired,
};

export default connect(mapStateToProps)(Login);
