import { LOGIN_ACTION, LOGOUT } from '../actions';

const INITIAL_STATE = {
  name: '',
  assertions: '',
  score: '',
  gravatarEmail: '',
};

const player = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case LOGIN_ACTION:
    return {
      ...state,
      name: action.payload.name,
      gravatarEmail: action.payload.email,
      score: 0,
    };
  case LOGOUT:
    return {
      ...state,
      name: '',
      gravatarEmail: '',
      score: 0,
    };

  default:
    return state;
  }
};

export default player;
